sqlMap.select:
select P.post_no as no, P.post_board_no as boardNo, B.board_name as boardName, P.post_title as title, P.post_important as important, P.post_deleted as deleted, P.post_writer_no as empNo, E.emp_name as empName, P.post_created_date as createdDate from (select row_number() over (order by post_no desc) row_numbers, post_no, post_board_no, post_title, post_important, post_deleted, post_writer_no, post_created_date from comm_posts where post_deleted != 'Y'
and post_board_no = #boardNo#
) P, comm_boards B, comm_employees E where P.post_board_no = B.board_no and P.post_writer_no = E.emp_no and P.row_numbers between #begin# and #end#
